import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Pressable, Button, Alert } from 'react-native';

export default function App( { navigation }) {
  const platformOptions = ['Pharmacy', 'Judiciary', 'Banking'];

  return (
    <View style={styles.container}>
      <Text>landing page</Text>
      <Button
        title="banking"
        onPress={() => navigation.navigate('banking')}
      />
      <Button
        title="pharmacy"
        onPress={() => navigation.navigate('pharmacy')}
      />
      <Button
        title="student judiciary"
        onPress={() => navigation.navigate('studentJudiciary')}
      />
    </View>
  );
}

function SignInScreen() {
  const [username, setUsername] = React.useState('');
  const [password, setPassword] = React.useState('');

  const { signIn } = React.useContext(AuthContext);

  return (
    <View>
      <TextInput
      style={styles.input}
        placeholder="   Username"
        value={username}
        onChangeText={setUsername}
      />
      <TextInput
      style={styles.input}
        placeholder="   Password"
        value={password}
        onChangeText={setPassword}
        secureTextEntry
      />
      <ChoosePlatform/>
      <Button title="Sign in" onPress={() => signIn({ username, password })} />
    </View>
  );
}

